import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { AdminService } from 'src/app/services/admin.service';
import { ProductoService } from 'src/app/services/producto.service';

declare var jQuery: any;
declare var $: any;
declare var iziToast: any;

@Component({
  selector: 'app-create-producto',
  templateUrl: './create-producto.component.html',
  styleUrls: ['./create-producto.component.css']
})
export class CreateProductoComponent implements OnInit {


  public producto : any = {
    categoria: ''
  };
  public file : File | undefined;
  public imgSelect: string | ArrayBuffer | null = 'assets/img/01.jpg';
  public token: string | null = null;
  public load_btn = false;
  public config_global : any = {}

  constructor(
    private _productoService : ProductoService,
    private _adminService : AdminService,
    private _router : Router
  ) { 
    this.token = this._adminService.getToken();
    this._adminService.obtener_config_publico().subscribe(
      response=>{
        this.config_global = response.data
      }
    )
  }

  ngOnInit(): void {
  }

  registro(registroForm: NgForm){
    if(registroForm.valid){
      if(this.file == undefined){

        iziToast.show({
          title: 'Error',
          titleColor: '#ff0000',
          color: '#FFF',
          class: 'text-danger',
          position: 'topRight',
          message: 'Debe subir una portada para registrar'
        });

      }else {
        console.log(this.producto);
      console.log(this.file);
      this.load_btn = true;

      this._productoService.registro_producto_admin(this.producto,this.file, this.token).subscribe(
        response=>{
          iziToast.show({
            title: 'Succes',
            titleColor: '#1DC74C',
            color: '#FFF',
            class: 'text-danger',
            position: 'topRight',
            message: 'Se Registro correctamente el producto'
          });

          this.load_btn = false;
          this._router.navigate(['/panel/productos']);
        },
        error=>{
          console.log(error);
          this.load_btn = false;
        }
      );
      }
      // Implementa la lógica para registrar el producto aquí
    } else {
      iziToast.show({
        title: 'Error',
        titleColor: '#ff0000',
        color: '#FFF',
        class: 'text-danger',
        position: 'topRight',
        message: 'Los datos del formulario no son validos!'
      });
      this.load_btn = false;


      $('#input-portada').text('seleccionar imagen');
      this.imgSelect = 'assets/img/01.jpg';
      this.file = undefined;///            

    }
  }

  fileChangeEvent(event: any): void {
    if (event.target.files && event.target.files[0]) {
        this.file = <File>event.target.files[0];
    } else {
        iziToast.show({
            title: 'ERROR NO ACCES',
            titleColor: '#ff0000',
            color: '#FFF',
            class: 'text-danger',
            position: 'topRight',
            message: 'No IMAGE NO ACCES..'
        });
    }

    // Validación de tamaño y tipo de archivo
    if (this.file && this.file.size <= 4 * 1024 * 1024){
      if(this.file.type === 'image/png' || 
         this.file.type === 'image/webp' || 
         this.file.type === 'image/jpg' || 
         this.file.type === 'image/gif' || 
         this.file.type === 'image/jpeg'){

        const reader = new FileReader();
        reader.onload = e => this.imgSelect = reader.result;
          // Asegúrate de que la imagen se lea correctamente
        console.log(this.imgSelect);
        reader.readAsDataURL(this.file);

        $('#input-portada').text(this.file.name)
        this.file = this.file

      } else {
        iziToast.show({
          title: 'ERROR',
          titleColor: '#ff0000',
          color: '#FFF',
          class: 'text-danger',
          position: 'topRight',
          message: 'Solo se permiten imágenes en formato PNG, JPEG, JPG, GIF o WEBP'
        });

        $('#input-portada').text('seleccionar imagen');
        this.imgSelect = 'assets/img/01.jpg';
        this.file = undefined;

      }
    } else {
      iziToast.show({
        title: 'ERROR TAMANIO',
        titleColor: '#ff0000',
        color: '#FFF',
        class: 'text-danger',
        position: 'topRight',
        message: 'La imagen es demasiado grande. Máximo 4MB'
      });

      $('#input-portada').text('seleccionar imagen')
      this.imgSelect = 'assets/img/01.jpg';
      this.file = undefined;

    }
    console.log(this.file)
 }
}
<app-sidebar></app-sidebar>

          <!-- Page title-->
          <div class="border-bottom pt-5 mt-2 mb-5">
            <h1 class="mt-2 mt-md-4 mb-3 pt-5">Productos</h1>
            <div class="d-flex flex-wrap flex-md-nowrap justify-content-between">
              <p class="text-muted">Este módulo lista todos los productos de la tienda.</p>

            </div>
          </div>
<form #registroForm="ngForm" (ngSubmit)="registro(registroForm)">

          <div class="card box-shadow-sm">
            <div class="card-header">
                <h5 style="margin-bottom: 0px;">Registro de producto</h5>
            </div>
            <div class="card-body">
                  <div class="row">
                    <div class="col-lg-8">
                        <div class="row">
                          <div class="col-lg-12 form-group">	
                              <label for="">Titulo de producto</label>
                              <input type="text" class="form-control" placeholder="Titulo de producto" required name="titulo" [(ngModel)]="producto.titulo">
                          </div>
                          <div class="col-lg-4 form-group">	
                              <label for="">Stock</label>
                              <input type="number" class="form-control" placeholder="Cantidad inicial" required name="stock" [(ngModel)]="producto.stock">
                          </div>
                          <div class="col-lg-4 form-group">	
                              <label for="">Precio</label>
                              <input type="number" class="form-control" placeholder="Precio" required name="precio" [(ngModel)]="producto.precio">
                          </div>
                          <div class="col-lg-4 form-group">	
                              <label for="">Categoría</label>
                              <select class="form-control custom-select" id="select-input" required name="categoria" [(ngModel)]="producto.categoria">
                                <option value="" selected disabled>Seleccionar</option>
                                <option value="Categoria 1" *ngFor="let item of con">Categoria 1</option>

                              </select>
                          </div>

                          <div class="col-lg-12 form-group">
                              <label for="">Descripción corta</label>
                              <textarea class="form-control" placeholder="Titulo de producto" required rows="5" name="descripcion" [(ngModel)]="producto.descripcion"></textarea>
                          </div>

                          <div class="col-lg-12 form-group">
                            <textarea class="form-control" required rows="15" name="contenido" [(ngModel)]="producto.contenido"></textarea>
                          </div>
                        </div>
                    </div>
                    <div class="col-lg-4">
                      <div class="row">
                        <div class="col-lg-12 form-group">	
                            <label for="">Portada</label>
                            <div class="custom-file">
                              <input class="custom-file-input" type="file" id="file-input" (change)="fileChangeEvent($event)">
                              <label class="custom-file-label" id="input-portada" for="file-input" style="white-space: nowrap;overflow:hidden;">Seleccionar imagen</label>
                            </div>
                        </div>
                        <div class="col-lg-12">
                          <img  [src]="imgSelect" class="img-thumbnail" alt="Rounded image">
                        </div>
                      </div>
                    </div>
                  </div>
            </div>
            <div class="card-footer">
                <button *ngIf="!load_btn"  class="btn btn-primary mr-3" type="submit">Crear producto</button>
                <button *ngIf="load_btn" type="button" class="btn btn-secondary" disabled>
                  <span class="spinner-grow spinner-grow-sm me-2" role="status" aria-hidden="true"></span>
                  Creando...
                </button>
                <a class="btn btn-warning" [routerLink]="['/panel/productos']" routerLinkActive="routerLinkActive">Regresar</a>
            </div>
      </div>    

</form>
