import { Component, OnInit } from '@angular/core';
import { GLOBAL } from 'src/app/services/GLOBAL';
import { ProductoService } from 'src/app/services/producto.service';

declare var iziToast: any;
declare var jQuery: any;
declare var $: any;

@Component({
  selector: 'app-index-producto',
  templateUrl: './index-producto.component.html',
  styleUrls: ['./index-producto.component.css']
})
export class IndexProductoComponent implements OnInit {

  public token: string | null;
  public filtro: string = '';
  public load_data = true;
  public productos: Array<any> = [];
  public url: string;
  public page = 1;
  public pageSize = 1000;
  public load_btn = false;

  constructor(
    private _productoService : ProductoService,
  ) {
    this.token = localStorage.getItem('token');
    this.url = GLOBAL.url;
  }

  ngOnInit(): void {
    //RECUERDENNNNN EL CRACKK
    this.init_data();

  }

  init_data(): void{
    this._productoService.listar_producto_admin(this.filtro, this.token).subscribe(
      response=>{
        console.log(response);
        this.productos = response.data;
        this.load_data = false;
      },
      error=>{
        console.log(error);
      }
    )
  }

  filtrar(){
    if(this.filtro){

      this._productoService.listar_producto_admin(this.filtro, this.token).subscribe(
        response=>{
          console.log(response);
          this.productos = response.data;
          this.load_data = false;
        },
        error=>{
          console.log(error);
        }
      )

    }else {
      iziToast.show({
        title: 'ERROR',
        titleColor: '#ff0000',
        color: '#FFF',
        class: 'text-danger',
        position: 'topRight',
        message: 'iNGRESA FILTRO PARA BUSCAR'
      });
    }
  }

  resetear(){
    this.filtro = '';
    this.init_data();
  }

  eliminar(id: string): void {
    this.load_btn = true;
    const token = this.token || ''; // Asigna un string vacío si `this.token` es null

    this._productoService.eliminar_producto_admin(id, token).subscribe(
      response => {
        iziToast.show({
          title: 'Succes',
          titleColor: '#1DC74C',
          color: '#FFF',
          class: 'text-danger',
          position: 'topRight',
          message: 'Se elimino correctamente el producto'
        });

        $('#delete-'+id).modal('hide');
        $('.modal-backdrop').removeClass('show');

        this.load_btn = false;

        this.init_data();

      },
      error => {
         iziToast.show({
          title: 'Succes',
          titleColor: '#1DC74C',
          color: '#FFF',
          class: 'text-danger',
          position: 'topRight',
          message: 'Ocurrio un error en el servidor'
        });
      }
    );
  }

}
<app-sidebar></app-sidebar>


          <!-- Page title-->
          <div class="border-bottom pt-5 mt-2 mb-5">
            <h1 class="mt-2 mt-md-4 mb-3 pt-5">Productos</h1>
            <div class="d-flex flex-wrap flex-md-nowrap justify-content-between">
              <p class="text-muted">Este módulo lista todos los productos de la tienda.</p>
              <p class="font-size-sm font-weight-medium pl-md-4">
                <a class="text-nowrap"[routerLink]="['/panel/productos/registro']">Registrar nuevo<i class="cxi-angle-right font-size-base align-middle ml-1"></i></a></p>
            </div>
          </div>

          <div class="card box-shadow-sm">
                <div class="card-header">
                    <h5 style="margin-bottom: 0px;">Filtro de productos</h5>
                </div>
                <div class="card-header">
                    <div class="row">
                        <div class="col-10">
                            <div class="input-group">
                                <input class="form-control" type="text" placeholder="Titulo del producto" name="filtro" [(ngModel)]="filtro">
                                <button class="btn btn-primary" type="button" (click)="filtrar()">Filtrar</button>
                                <button class="btn btn-info" type="button" (click)="resetear()">Resetear</button>
                            </div>
                        </div>
                    </div>

                </div>

                <div class="card-body">
                    <div class="table-responsive">
                        <table class="table table-bordered">
                          <thead class="thead-dark">
                            <tr>

                              <th>Titulo</th>
                              <th>Stock</th>
                              <th>Precio</th>
                              <th>Categorias</th>
                              <th>Ventas</th>
                              <th>Op</th>
                            </tr>
                          </thead>
                          <tbody *ngIf="load_data">
                            <tr>
                              <td class="text-center" colspan="7"> 
                                <!-- Custom size border spinner -->
                                  <div class="spinner-border" style="width: 3rem; height: 3rem;" role="status">
                                    <span class="visually-hidden">Loading...</span>
                                  </div>
                              </td>
                            </tr>
                          </tbody>

                          <ng-container *ngIf="!load_data">
                            <tbody *ngFor="let item of productos| slice: (page-1)*pageSize : (page-1) *pageSize + pageSize;">
                              <tr>
                               <td>
                                <h6 style="padding-bottom: 0;margin-bottom: 0;">
                                  <!-- Square thumbnail -->
                                  <img style="width: 50px " [src]="url + 'obtener_portada/' +item.portada" class="img-thumbnail rounded-0" alt="Square image">
                                  <td>{{item.titulo}}</td>
                                </h6>
                               </td>
                                <td>{{item.stock}}</td>
                                <td>{{item.precio}}</td>
                                <td>{{item.categoria}}</td>
                                <td>{{item.nventas}}</td>

                                <div class="btn-group dropdown d-inline-block mb-3 me-2">
                                  <button class="btn btn-outline-secondary btn-sm dropdown-toggle" type="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">OPCIONES</button>
                                  <div class="dropdown-menu">
                                      <a class="dropdown-item" [routerLink]="['/panel/productos',item._id]" routerLinkActive="routerLinkActive">Editar producto</a>
                                      <a class="dropdown-item" style="cursor: pointer;" data-toggle="modal" [attr.data-target]="'#delete-'+item._id">Eliminar producto</a>
                                      <a class="dropdown-item" [routerLink]="['/panel/productos/inventario',item._id]" routerLinkActive="routerLinkActive">Inventario</a>
                                      <a class="dropdown-item" [routerLink]="['/panel/productos/variedades',item._id]" routerLinkActive="routerLinkActive">Variedades</a>

                                  </div>
                              </div>
                              <div class="modal fade" id="delete-{{item._id}}" tabindex="-1" style="display: none;" aria-hidden="true">
                                <div class="modal-dialog modal-dialog-centered" role="document">
                                  <div class="modal-content">
                                    <div class="modal-header">
                                      <h4 class="modal-title">Confirmacion de eliminacion.</h4>
                                      <button class="btn-close" type="button" data-bs-dismiss="modal" aria-label="Close"></button>
                                    </div>
                                    <div class="modal-body">
                                      <p> {{item.titulo}},Desea Eliminar este producto?</p>
                                    </div>
                                    <div class="modal-footer">
                                      <button class="btn btn-secondary btn-sm" type="button" data-bs-dismiss="modal">Cancelar</button>
                                      <button *ngIf="!load_btn" class="btn btn-primary btn-sm" (click)="eliminar(item._id)" type="button">Si, Eliminar</button>
                                      <button *ngIf="load_btn" type="button" class="btn btn-secondary" disabled>
                                        <span class="spinner-grow spinner-grow-sm me-2" role="status" aria-hidden="true"></span>
                                        Loading...
                                      </button>
                                    </div>
                                  </div>
                                </div>
                              </div>

                              </tr>
                            </tbody>
                          </ng-container>


                        </table>
                    </div>
                </div>
                <div class="card-footer">
                    <ngb-pagination>
                        [(page)]="page"
                        [pageSize]="pageSize"
                        [collectionSize]="productos.length"
                    </ngb-pagination>
                </div>
          </div>
