import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ProductoService } from 'src/app/services/producto.service';

declare var iziToast: any;

@Component({
  selector: 'app-variedad-producto',
  templateUrl: './variedad-producto.component.html',
  styleUrls: ['./variedad-producto.component.css']
})
export class VariedadProductoComponent {

  // Definimos una lista de objetos con la información de cada producto
  public producto : any= {};
  public id: string = ''; // Inicializa como una cadena vacía
  public token: string | null;
  public nueva_variedad = '';

  constructor(
    private _route: ActivatedRoute,
    private _productoService: ProductoService
  ){

    this.token = localStorage.getItem('token');
    this._route.params.subscribe(
      params => {
        this.id = params['id']; // Asigna el id obtenido de la ruta a la variable id


        this._productoService.obtener_producto_admin(this.id, this.token).subscribe(
          response=>{
            if(response.data == undefined){
              this.producto = undefined;
            }else {

              this.producto = response.data;         
            }
            console.log(this.producto);

          },
          error=>{
            console.log(error);
          }
        )
      }
    );
  }

  ngOnInit(): void {

  }

  agregar_variedad(){
    if(this.nueva_variedad){

      this.producto.variedades.push({titulo: this.nueva_variedad})
      this.nueva_variedad = '';
    }else{
      iziToast.error({
        title: 'Error',
        titleColor: '#FF0000',
        color: '#FFF',
        class: 'text-danger',
        message: 'El campo de la variedad debe ser completado',
        position: 'topRight'
      });

    }
  }


  eliminar_variedad(idx:number){
    this.producto.variedades.splice(idx, 1)
  }

  actualizar(){
    if(this.producto.titulo_variedad){

    }
  }

}
<app-sidebar></app-sidebar>
          <!-- Page title-->
          <div class="border-bottom pt-5 mt-2 mb-5">
            <h1 class="mt-2 mt-md-4 mb-3 pt-5">Productos</h1>
            <div class="d-flex flex-wrap flex-md-nowrap justify-content-between">
              <p class="text-muted">Este módulo se estableve las configuraciones de la tineda.</p>

            </div>
          </div>


  <div class="card box-shadow-sm">
    <div class="card-header">
        <h5 style="margin-bottom: 0px;">Variedad de producto</h5>
    </div>

    <div class="card-body">
      <div style="max-width: 48rem;">


        <div class="form-group row align-items-center">
          <label class="col-md-3 col-form-label font-weight-medium" for="text-input">Titulo de la variedad</label>
          <div class="col-md-9">
            <input class="form-control" required type="text" id="text-input" placeholder="Titulo de la variedad" name="titulo_variedad" [(ngModel)]="producto.titulo_variedad">
          </div>
        </div>

        <div class="form-group row align-items-center">
          <label class="col-md-3 col-form-label font-weight-medium" for="text-input">Variedades</label>
          <div class="col-md-9">           
                <div class="input-group">
                    <input class="form-control" type="text" placeholder="Nueva variedad" name="nueva_variedad" [(ngModel)]="nueva_variedad">
                    <div class="input-group">
                      <button class="btn btn-primary" type="button" (click)="agregar_variedad()">Agregar</button>
                    </div>
                </div>
          </div>

          <div class="col-md-3">

          </div>
          <div class="col-md-9">
            <div class="table-responsive mt-3">
              <table class="table table-sm" *ngIf="producto.variedades">
                <thead class="thead-dark">
                  <tr>

                    <th>Titulo</th>
                    <th>Eliminar</th>          
                  </tr>
                </thead>
                <ng-container *ngIf="producto.variedades.length== 0">
                  <tbody>
                    <tr>
                      <td colspan="2" class="text-center">
                        <span class="text-muted">No hay categorias disponibles</span>
                      </td>

                    </tr>
                  </tbody>
                </ng-container>

                <ng-container *ngIf="producto.variedades.length>=1">
                  <tbody *ngFor="let item of producto.variedades; let indice = index">
                    <tr>
                      <td>{{item.titulo}}</td>
                       <td><a style="cursor: pointer;" class="text-danger" (click)="eliminar_variedad(indice)">[Eliminar]</a></td>
                    </tr>
                  </tbody>
                </ng-container>
              </table>
            </div>
          </div>

        </div>

      </div>

    </div>
    <div class="card-footer">
      <button class="btn btn-primary mr-3" type="submit">Guardar cambios</button>
    </div>
</div>
