import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ProductoService } from 'path-to-your-service'; // Asegúrate de importar correctamente tu servicio

@Component({
  selector: 'app-variedad-producto',
  templateUrl: './variedad-producto.component.html',
  styleUrls: ['./variedad-producto.component.css']
})
export class VariedadProductoComponent implements OnInit {

  public producto: any = {};
  public inventarios: any[] = [];
  public id: string = '';
  private token: string = ''; // Asegúrate de inicializar o asignar el token correctamente

  constructor(
    private _route: ActivatedRoute,
    private _productoService: ProductoService
  ) { 
    this._route.params.subscribe(params => {
      this.id = params['id'];
      this.cargarProducto(this.id);
    });
  }

  ngOnInit(): void {
    // Aquí puedes realizar otras inicializaciones necesarias
  }

  cargarProducto(id: string): void {
    this._productoService.obtener_producto_admin(id, this.token).subscribe(
      response => {
        if (response.data === undefined) {
          this.producto = undefined;
        } else {
          this.producto = response.data;
          this.cargarInventarios(this.producto._id);
        }
      },
      error => {
        console.error('Error al obtener el producto:', error);
      }
    );
  }

  cargarInventarios(productoId: string): void {
    this._productoService.listar_inventario_producto_admin(productoId, this.token).subscribe(
      response => {
        this.inventarios = response.data;
        console.log('Inventarios:', this.inventarios);
      },
      error => {
        console.error('Error al listar los inventarios:', error);
      }
    );
  }
}
